import Vue from "vue"
import Vuex from "vuex"


export default new Vuex.Store({
    actions: {
        setLang(ctx){
            ctx.commit('setLangIndex', index)
        }
    },
    mutations: {
        setLangIndex(state, index) {
            state.langIndex = index;
        },
    },
    state: {
        langIndex: 1
    },
    getters: {
        getLangIndex(state) {
            return state.langIndex
        }
    }
})
