export default {
    props: {
        titles: {
            type: Array,
            default: () => []
        },
        subtitles: {
            type: Array,
            default: () => []
        }
    },
    data() {
        return {
            index: 0,
            isHoveredNext: false,
            isHoveredPreviuse: false,
            isHoveredButton: false,
            buttonText: ["Подробнее", "More info", "plus d'info"]
        };
    },
    methods: {
        nextButton: function () {
            if (this.index + 1 < this.titles.length) {
                this.index++;
                this.index = Math.round(this.index % this.titles.length);
            }
        },
        previusButton: function () {
            if (this.index > 0) {
                this.index--;
                this.index = Math.round(this.index % this.titles.length);
            }
        }
    }
}
