import Vue from "vue"
import Vuex from "vuex"

Vue.use(Vuex)

export default new Vuex.Store({
    actions: {},
    mutations: {
    setCurrentLanguage(state, index) {
        state.currentLanguage = index;
        },
    },
    state: {
        langIndex: 0
    },

 })
