import Vue from "vue"
import Vuex from "vuex"

Vue.use(Vuex)

export default new Vuex.Store({
    actions: {},
    mutations: {
        setLangIndex(state, index) {
            state.langIndex = index;
        },
    },
    state: {
        langIndex: 0
    },
    getters: {
        allPosts(state){
            return state.post
        }
    }
 })
